body.raw:
{
    "channels": [
        {
            "name": "whatsapp",
            "from": "{{from}}"
        }
    ],
    "recipient": {
        "to": [
            "{{receiver}}"
        ]
    },
    "message": {
        "type": "interactive",
        "payload": {
            "header": {
                "type": "text",
                "payload": {
                    "text": "header text"
                }
            },
            "body": {
                "type": "text",
                "payload": {
                    "text": "body text"
                }
            },
            "footer": {
                "type": "text",
                "payload": {
                    "text": "footer text"
                }
            },
            "choices": [
                {
                    "type": "url",
                    "payload": {
                        "url": "https://example.com",
                        "title": "Go to url",
                        "id": "unique-id"
                    }
                },
                {
                    "type": "text",
                    "payload": {
                        "title": "Just Text",
                        "id": "unique-id1"
                    }
                },
                {
                    "type": "reply",
                    "payload": {
                        "title": "click to accept",
                        "id": "unique-id2"
                    }
                }
            ]
        }
    }
}
